import React, { useEffect, Fragment } from 'pages/tagFeed/node_modules/react'
import {stringify} from 'pages/tagFeed/node_modules/query-string'

import useFetch from 'pages/tagFeed/node_modules/hooks/useFetch'
import Feed from 'pages/tagFeed/node_modules/components/feed'
import Pagination from 'pages/tagFeed/node_modules/components/pagination'
import {getPaginator, limit} from 'pages/tagFeed/node_modules/utils'
import PopularTags from 'pages/tagFeed/node_modules/components/popularTags'
import Loading from 'pages/tagFeed/node_modules/components/loading'
import ErrorMessage from 'pages/tagFeed/node_modules/components/errorMessage'
import FeedToggler from 'pages/tagFeed/node_modules/components/feedToggler'

const TagFeed = ({location, match}) => {
    const tagName = match.params.slug
    console.log('tagName', tagName)
    const {offset, currentPage} = getPaginator(location.search)
    const stringyfiedParams = stringify({
        limit,
        offset,
        tag: tagName
    })
    const apiUrl = `/articles?${stringyfiedParams}`
    const [{response, isLoading, error}, doFetch] = useFetch(apiUrl)
    const url = match.url

    useEffect(() => {
        doFetch()
    }, [doFetch, currentPage, tagName])

    return (
        <div className='home-page'>
            {/* Создаем баннер */}
            <div className='banner'>
                <div className='container'>
                    <h1>Medium clone</h1>
                    <p>A place to share knowledge</p>
                </div>
            </div>   
            {/* Выводим контент страницы */}
            <div className='container page'>
                <div className='row'>
                    <div className='col-md-9 '>
                        <FeedToggler tagName={tagName}/>
                        {isLoading && <Loading/>}
                        {error && <ErrorMessage/>}
                        {!isLoading && response && (
                            <Fragment>
                                <Feed articles={response.articles}/>
                                <Pagination 
                                    total={response.articlesCount} 
                                    limit={limit} 
                                    url={url} 
                                    currentPage={currentPage}
                                />
                            </Fragment> 
                        )}
                    </div>
                    <div className='col-md-3'>
                        <PopularTags/>
                    </div>
                </div>
            </div>
        </div>         
    )
}

export default TagFeed

